import {StatusBar} from 'expo-status-bar';
import {StyleSheet, Text, View, Button} from 'react-native';
import {useAppDispatch, useAppSelector} from '../redux/store';
import {setMessage} from '../redux/initSlice';
import {ImageBackground} from 'react-native';

/* const BgImage = () => (
  <Image
    source={require('../assets/cl_start_bg.webp')}
    style={{width: '100%', height: '40%'}}
  />
); */
// const BgImage2 = () => <Image source={require('../assets/')} />;

export default function HomeScreen({navigation}) {
  const dispatch = useAppDispatch();
  const apiOrigin = process.env.EXPO_PUBLIC_API_ORIGIN;
  const {message} = useAppSelector((state) => state.message);
  return (
    <>
      {/* <View
        style={{
          width: '100%',
          height: 400,
        }}
      > */}
      <ImageBackground
        source={require('../assets/cl_start_bg.webp')}
        // style={{width: '100%', height: 'auto'}}
        resizeMode="cover"
        style={{
          flex: 1,
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          width: '100%',
          height: 400,
          // opacity: 0.5,
        }}
      >
        <View
          style={{
            height: '100%',
            width: '100%',
            opacity: 0.6,
            backgroundColor: 'black',

            flex: 1,
            marginTop: 8,
            marginBottom: 8,
            // minHeight: 200,
            alignItems: 'center',
          }}
        >
          <ImageBackground
            source={require('../assets/cl_logo_2_white.png')}
            style={{
              // width: 30, height: 40,
              width: 100,
              height: 40,
              maxWidth: '100%',
              marginTop: 30,
              marginBottom: 10,
            }}
            resizeMode="cover"
          />
          <View
            style={{
              borderBottomColor: 'orange',
              borderBottomWidth: 3,
              width: 60,
            }}
          />
          {/* <Text>Профессиональный клининг</Text> */}
          <Text
            style={{
              color: 'white',
              fontSize: 38,
              lineHeight: 84,
              fontWeight: 'bold',
              // backgroundColor: '#000000c0',
              marginBottom: 10,
            }}
          >
            Профессиональный клининг
          </Text>
          <Text style={{color: 'white', fontSize: 20, marginBottom: 10}}>
            - гарантия качества
          </Text>{' '}
          <Text style={{color: 'white', fontSize: 20, marginBottom: 10}}>
            - фиксированная цена
          </Text>
          <Text style={{color: 'white', fontSize: 20, marginBottom: 10}}>
            - частные и коммерческие объекты
          </Text>
          <Text style={{color: 'white', fontSize: 20, marginBottom: 10}}>
            - подготовленные специалисты
          </Text>
          <View>
            <Button
              title="Заказ"
              onPress={() => navigation.navigate('Order')}
            />
          </View>
        </View>
      </ImageBackground>
      {/* </View> */}

      <View style={styles.container}>
        <Text>Home Screen</Text>

        <Text>EXPO_PUBLIC_API_ORIGIN: {apiOrigin}</Text>
        <StatusBar style="auto" />
        <Button
          title="Go to Details"
          onPress={() => navigation.navigate('Details')}
        />
        <Text style={{marginTop: 5}}>Redux msg: {message}</Text>
        <Button
          title="Set new message"
          onPress={() => dispatch(setMessage('New Message from Component'))}
        />
      </View>
      <View style={styles.container2}>
        <View style={[styles.box, {backgroundColor: 'powderblue'}]} />
        <View style={[styles.box, {backgroundColor: 'skyblue'}]} />
        <View
          style={[
            styles.box,
            {
              backgroundColor: 'steelblue',
              width: 'auto',
              minWidth: 50,
            },
          ]}
        />
      </View>
      <Text>End</Text>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
    // textAlign: 'center',
    border: '2px solid green',
    maxWidth: 500,
  },

  image: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    height: 'auto',
    opacity: 0.9,
  },

  box: {
    width: 50,
    height: 50,
  },

  container2: {
    flex: 1,
    marginTop: 8,
    backgroundColor: 'aliceblue',
    minHeight: 200,
    alignItems: 'center',
  },
});
